macro.dbt_expectations.default__test_exp.macro_sql:
{% macro default__test_expect_grouped_row_values_to_have_recent_data(model,
                                                                        group_by,
                                                                        timestamp_column,
                                                                        datepart,
                                                                        interval,
                                                                        row_condition) %}
with latest_grouped_timestamps as (

    select
        {{ group_by | join(",") ~ "," if group_by }}
        max(1) as join_key,
        max(cast({{ timestamp_column }} as {{ dbt_expectations.type_timestamp() }})) as latest_timestamp_column
    from
        {{ model }}
    where
        -- to exclude erroneous future dates
        cast({{ timestamp_column }} as {{ dbt_expectations.type_timestamp() }}) <= {{ dbt_date.now() }}
        {% if row_condition %}
        and {{ row_condition }}
        {% endif %}

    {% if group_by -%}
    {{  dbt_expectations.group_by(group_by | length) }}
    {%- endif %}
),
total_row_counts as (

    select
        {{ group_by | join(",") ~ "," if group_by }}
        max(1) as join_key,
        count(*) as row_count
    from
        latest_grouped_timestamps
    {% if group_by -%}
    {{  dbt_expectations.group_by(group_by | length) }}
    {%- endif %}


),
outdated_grouped_timestamps as (

    select *
    from
        latest_grouped_timestamps
    where
        -- are the max timestamps per group older than the specified cutoff?
        latest_timestamp_column <
            cast(
                {{ dbt.dateadd(datepart, interval * -1, dbt_date.now()) }}
                as {{ dbt_expectations.type_timestamp() }}
            )

),
validation_errors as (

    select
        r.row_count,
        t.*
    from
        total_row_counts r
        left join
        outdated_grouped_timestamps t
        on
            {% for g in group_by %}
            r.{{ g }} = t.{{ g }} and
            {% endfor %}
            r.join_key = t.join_key
    where
        -- fail if either no rows were returned due to row_condition,
        -- or the recency test returned failed rows
        r.row_count = 0
        or
        t.join_key is not null

)
select * from validation_errors
{% endmacro %}

{% macro default__test_expect_row_values_to_have_recent_data(model, column_name, datepart, interval, row_condition) %}
{%- set default_start_date = '1970-01-01' -%}
with max_recency as (

    select max(cast({{ column_name }} as {{ dbt_expectations.type_timestamp() }})) as max_timestamp
    from
        {{ model }}
    where
        -- to exclude erroneous future dates
        cast({{ column_name }} as {{ dbt_expectations.type_timestamp() }}) <= {{ dbt_date.now() }}
        {% if row_condition %}
        and {{ row_condition }}
        {% endif %}
)
select
    *
from
    max_recency
where
    -- if the row_condition excludes all rows, we need to compare against a default date
    -- to avoid false negatives
    coalesce(max_timestamp, cast('{{ default_start_date }}' as {{ dbt_expectations.type_timestamp() }}))
        <
        cast({{ dbt.dateadd(datepart, interval * -1, dbt_date.now()) }} as {{ dbt_expectations.type_timestamp() }})

{% endmacro %}

